package templates

const DATABASE_QUERIES_TokenTemplate = `
-- Generated by egg v0.0.1


-- name: FindTokenByToken :one
SELECT *
    FROM tokens 
    WHERE token = $1
    AND  expiration_datetime > now();

-- name: FindTokenByUserId :one
SELECT *
    FROM tokens 
    WHERE user_id = $1
    AND  expiration_datetime > now();

-- name: UpdateTokenByUserId :exec
UPDATE tokens 
    SET token = $1, expiration_datetime = $2
    WHERE user_id = $3;

-- name: CreateToken :one
INSERT INTO tokens (
    user_id, expiration_datetime, token
) VALUES ( $1, $2, $3 )
RETURNING *;
`
